import React, { useState } from "react";

const Mouse = () => {
  let circle = document.getElementsByClassName("circle");
  const mouveMyMouse = e => {
    let attrX = e.pageX + "px";
    let attrY = e.pageY + "px";

    circle.style.left = attrX;
    circle.style.top = attrY;
  };

  window.addEventListener("mousemove", mouveMyMouse);
  return (
    <div>
      <div
        className="circle"
        style={{
          height: "70px",
          width: "70px",
          borderRadius: 300,
          border: "2px solid red",
          position: "absolute"
        }}
      ></div>
    </div>
  );
};

export default Mouse;
