import React, { Component } from "react";

class Mouse extends Component {
  state = {
    base: [
      {
        height: "70px",
        width: "70px",
        borderRadius: 300,
        border: "2px solid red",
        position: "absolute"
      }
    ]
  };

  render() {
    let { base } = this.state;
    let circle = document.querySelector(".circle");
    const mouveMe = e => {
      circle.style.top = e.pageX + " px";
    };
    window.addEventListener("mousemove", mouveMe);

    return (
      <div>
        <div>
          <div className="circle" style={base[0]}></div>
        </div>
      </div>
    );
  }
}

export default Mouse;
