import React, { Component } from "react";

class Chargement extends Component {
  render() {
    let myFlex =
      "blocChargement display-1 d-flex justify-content-center align-content-center m-auto w-100 fw-bold";
    let bgChargement = document.querySelector(".bgChargement");

    document.body.addEventListener("click", () => {
      bgChargement.classList.add("fonduChargement");
    });
    return (
      <div className="bgChargement d-flex">
        <div className={myFlex}>
          <p className>C</p>
          <p>H</p>
          <p>A</p>
          <p>R</p>
          <p>G</p>
          <p>E</p>
          <p>M</p>
          <p>E</p>
          <p>N</p>
          <p>T</p>
        </div>
      </div>
    );
  }
}

export default Chargement;
